    static String ppp = "**";
    static String sss = "";

    public boolean isMatch(String s, String p) {
        if(s == null || p == null) {
            return false;
        }
        if(s.length() == 0 && p.length() == 0) {
            return true;
        }
        if(p.length() == 0) {
            return false;
        }

        // deal with head, removing chars before the first star
        int firstStarIndex = 0, i = 0;
        for(; i < p.length(); i++) {
            if(p.charAt(i) == '*') {
                firstStarIndex = i;
                break;
            }
            if(i < s.length() && (p.charAt(i) == s.charAt(i) || p.charAt(i) == '?')) {
                if(i == s.length()-1 && i == p.length()-1) {    // no star in the pattern
                    return true;
                }
                continue;
            }
            return false;
        }
        if(p.charAt(firstStarIndex) != '*') {   // no star found
            return s.equals(p);
        }
        s = s.substring(firstStarIndex);
        p = p.substring(firstStarIndex);

        // deal with tail
        int lastStarIndex = p.length()-1, count = -1;
        i = p.length()-1;
        for(; i >= 0; i--) {
            count++;
            if(p.charAt(i) == '*') {
                lastStarIndex = i;
                break;
            }
            if(s.length()-1-count >= 0 && (p.charAt(i) == s.charAt(s.length()-1-count) || p.charAt(i) == '?')) {
                continue;
            }
            return false;
        }
        s = s.substring(0, s.length()-1-count+1);
        p = p.substring(0, lastStarIndex+1);

        String[] pattern = p.split("[*]");
        for(String temp:pattern){
            if(!temp.isEmpty()){
                int next = getFirstIndex(s, temp);
                if(next<0)
                    return false;
                else
                    s=s.substring(next);
            }
        }
        return true;
    }

    public int getFirstIndex(String s, String p){
        if(s.length() < p.length()) {
            return -1;
        }

        for(int start = 0; start < s.length()-p.length()+1; start++) {
            int cur = start;
            while(cur < s.length()) {
                if(cur-start > p.length()-1 || (s.charAt(cur) != p.charAt(cur-start) && p.charAt(cur-start) != '?')) {
                    break;
                }
                cur++;
            }
            if(cur-start == p.length()) {
                return cur;
            }
        }

        return -1;
    }

    public boolean isMatch1(String s, String p) {
        if(s == null || p == null) {
            return false;
        }
        if(s.length() == 0 && p.length() == 0) {
            return true;
        }

        int starCount = 0;
        boolean haveStarBefore = false;
        int firstStarIndex = 0;
        for(int i = 0; i < p.length(); i++) {
            if(p.charAt(i) == '*') {
                starCount++;
                if(!haveStarBefore) {
                    firstStarIndex = i;
                }
                haveStarBefore = true;
            }
            else {
                if(!haveStarBefore) {
                    if(i < s.length()) {
                        if(p.charAt(i) != s.charAt(i)) {
                            return false;
                        }
                    }
                    else {
                        return false;
                    }
                }
            }
        }
        s = s.substring(firstStarIndex);
        p = p.substring(firstStarIndex);

        int maxStarOffset = s.length() - (p.length() - starCount);

        boolean[][] m = new boolean[s.length()+1][2];   // space optimization: only need two column
        m[s.length()][p.length()%2] = true;

        for(int i = s.length(); i >= 0; i--) {
            for(int j = p.length()-1; j >= 0; j--) {
                if(!m[i][j%2]) {
                    if(i == s.length()) {
                        m[i][j%2] = (p.charAt(j) == '*' && m[i][(j+1)%2]);
                    }
                    else {
                        m[i][j%2] |= p.charAt(j) == s.charAt(i) && m[i+1][(j+1)%2];
                        if(p.charAt(j) == '*') {
                            int tempi = i;
                            while(!m[i][j%2] && tempi <= s.length() && tempi - i <= maxStarOffset) {
                                m[i][j%2] |= m[tempi][(j+1)%2];
                                tempi++;
                            }
                        } else {
                            if(p.charAt(j) == '?') {
                                m[i][j%2] |= m[i+1][(j+1)%2];
                            }
                        }
                    }
                    if(j == 0 && m[i][j%2]) {
                        return true;
                    }
                }
            }
        }
        return m[0][0];
    }

